<template>
    <div id="general">



        <div class="center">
            <section id="content">
                <h1 class="subheader">Peliculas</h1>

                <div class="misDatos" v-if="misDatos">
                    <p v-html="misDatos"></p>
                    <p>{{web | mayusculas | concatenaYear('Este es el mejor anio')}}</p>
                </div>

                <div class="favorita" v-if="favorita">
                    Pelicula favorita:
                    <h3> {{favorita.title}}</h3>
                </div>

                <!--Listado articulos-->
                <div id="articles">

                    <!--v-for="pelicula in peliculasModificadas" v-bind:key="pelicula.title"-->

                    <div v-for="pelicula in peliculasMayuscula" v-bind:key="pelicula.title">
                        <Pelicula
                                :pelicula="pelicula"
                                v-on:favorita="haLlegadoLaPeliculaFavorita"
                        ></Pelicula>
                    </div>


                </div>

            </section>
            <Sidebar/>
        </div>
    </div>
</template>

<script>

    import Sidebar from "./Sidebar";

    import Pelicula from "./Pelicula";

    export default {
        name: "Peliculas",

        components: {
            Sidebar,
            Pelicula
        } ,

        methods: {
            haLlegadoLaPeliculaFavorita(favorita) {
                console.log('se ha ejecutado el evento en el padre');
                this.favorita = favorita;
            }
        },

        //filtros (como pipes en angular)
        filters: {
            mayusculas(value) {
                return value.toUpperCase();
            },
            concatenaYear(value, message) {
                var date = new Date();
                return value + ' ' + date.getFullYear() + ' ' + message;
            },

        },


        //propiedad computada
        computed: {
            peliculasMayuscula() {
                var peliculasModificadas = this.peliculas;
                for(var i = 0; i < this.peliculas.length; i++){
                    peliculasModificadas[i].title = peliculasModificadas[i].title.toUpperCase();
                }
                return peliculasModificadas;
            },

            misDatos() {
                return this.nombre + ' ' + this.apellidos + '<br/>' + '<strong>sitio web:</strong>  ' + this.web;
            },

        },




        data(){
            return {
                nombre: 'Xavier',
                apellidos: 'Decker Zambrano',
                web: 'proyecto-vue.com',
                favorita: null,
                peliculas: [
                    {title: 'Batman vs Superman', year: 2017, image:'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcRa-j9VE7_JRdRf1CeLqHfnLl421_1ZPgNmTf38YHd3F4XhGE9S&usqp=CAU'},
                    {title: 'Gran Torino', year: 2015, image:'data:image/jpeg;base64,'},
                    {title: 'Spiderman', year: 2005, image:'data:image/jpeg;base64,'}
                ]
            }
        }

    }

</script>

